targets.query:
service_type : DD AND service_id:${dd_service} AND mission:${mission} AND satellite_unit:${satellite_unit} AND product_level:${product_level} AND product_type:${product_type} 
AND ( ((timeliness:NRT OR timeliness: NRT-PT) AND from_sensing_timeliness: [10800000000 TO *]) OR
((timeliness:NTC) AND from_sensing_timeliness: [28800000000 TO *])
) AND timeliness:$timeliness
